    function board_move(vx,vy,ctx,canvas){
      var o = ctx.cake;          
      if(o.myop=='pencil'){
        o.myfig.x += (vx);
        o.myfig.y += (vy);
      } 
      if(o.myop=='ball'){
        if(o.hit_ball){
          var m0 = new DOMMatrix([1,0,0,1,0,0]);
          m0.translateSelf(vx,vy);
          var [cornerx,cornery] = ball_getcornerxy(o.hit_ball);
          var [a,b,c,d,e,f] = o.hit_ball.transform;
          var matrix = new DOMMatrix([a,b,c,d,e,f]);
          m0.multiplySelf(matrix);
          var {a,b,c,d,e,f} = m0;
          o.hit_ball.transform = [a,b,c,d,e,f];
          o.hit_ball.pivot_x += vx;
          o.hit_ball.pivot_y += vy;
        }
      }
    }
    function board_skewX(v,ctx,canvas){
      let o = ctx.cake;
      if(o.myop=='pencil'){
        o.myfig.skewX += v;
      }
      if(o.myop=='ball'){
        if(o.hit_ball){
          var [cornerx,cornery] = ball_getcornerxy(o.hit_ball);
          var [a,b,c,d,e,f] = o.hit_ball.transform;
          var matrix = new DOMMatrix([a,b,c,d,e,f]);
          matrix.translateSelf(cornerx,cornery);
          matrix.skewXSelf(v);//make sure it is in DEGREES
          matrix.translateSelf(-cornerx,-cornery);
          var {a,b,c,d,e,f} = matrix;
          o.hit_ball.transform = [a,b,c,d,e,f];
        }
      }
    }
