    function board_dup(ctx,canvas){
      var o = ctx.cake;
      if(o.myop=='ball'){
        if(o.hit_ball){
          var ball = o.hit_ball;
          var transform = [...ball.transform];
          var corner_i = ball.corner_i;//int
          var pivot_x = ball.pivot_x;//float
          var pivot_y = ball.pivot_y;//float
          var color = ball.color;//string
          var w = ball.w;//number
          var h = ball.h;//number
          var points = [];
          ball.points.forEach((pt,i)=>{
            points.push({...pt});
          });
          let ball_dup = {points,transform,corner_i,pivot_x,pivot_y,color,w,h};
          o.balls.push(ball_dup);
        }
      }
    }
