    function board_handle_keydown(shiftKey,keycode,ctx,canvas){
      console.log('board_handle_keydown',keycode);
      switch(keycode){
      ///FOR PENCIL COLORS ROYALBLUE
      case 'KeyX':
      	if(shiftKey){
          board_remove(ctx,canvas);
        }else{
        }
        break;
      ///STROKES
      case 'Backquote':
        board_toggle_strokeid(ctx,canvas);
        break;
      case 'Backslash':
        if(shiftKey){
          board_revundo(ctx,canvas);
        }else{
          board_undo(ctx,canvas);
        }
        break;
      case 'Slash':
        if(shiftKey){
          board_reset(ctx,canvas);
        }else{
          board_action(ctx,canvas);
        }
        break;
      case 'BracketLeft':
        if(shiftKey){
          ///ZOOM_OUT (become smaller)
          let o = ctx.cake;                
          o.zoom /= 1.05;
        }else{
          ///FONT
          let o = ctx.cake;                
          o.myfs -= 1;
          o.myfs = Math.max(2,o.myfs);
        }
        break;
      case 'BracketRight':
        if(shiftKey){
          ///ZOOM_IN (becomes larger)
          let o = ctx.cake;                
          o.zoom *= 1.05;
        }else{
          ///FONT
          let o = ctx.cake;                
          o.myfs += 1;
          o.myfs = Math.min(30,o.myfs);
        }
        break;
      case 'KeyH':
        ///LEFT
        board_move(-1,0,ctx,canvas);
        break;
      case 'KeyJ':
        ///DOWN
        board_move(0,+1,ctx,canvas);
        break;
      case 'KeyK':
        ///UP
        board_move(0,-1,ctx,canvas);
        break;
      case 'KeyL':
        ///RIGHT
        board_move(+1,0,ctx,canvas);
        break;
      case 'KeyR':
        if(shiftKey){
          ///COUNTER-CLOCKWISE ROTATE
          board_rotate(-1,ctx,canvas);
        }else{
          ///CLOCKWISE ROTATE
          board_rotate(+1,ctx,canvas);
        }
        break;
      case 'KeyS':
        if(shiftKey){
          board_scale(1.01,1.01,ctx,canvas);
        }else{
          board_scale(1/1.01,1/1.01,ctx,canvas);
        }
        break;
      case 'KeyW':
        ///SCALE-HORIZONTAL
        if(shiftKey){
          board_scale(1.01,1,ctx,canvas);
        }else{
          board_scale(1/1.01,1,ctx,canvas);
        }
        break;
      case 'KeyE':
        ///SCALE-VERTICAL
        if(shiftKey){
          board_scale(1,1.01,ctx,canvas);
        }else{
          board_scale(1,1/1.01,ctx,canvas);
        }
        break;
      case 'KeyT':
        ///SKEW-HORIZONTAL
        board_skewX((shiftKey)?(-1):(+1), ctx,canvas);
        break;
      ///SKEW-Y  
      case 'KeyY':
        ///SKEW-VERTICAL
        board_skewY((shiftKey)?(-1):(+1), ctx,canvas);
        break;
      case 'KeyA':
        if(shiftKey){
          board_add(ctx,canvas);
        }else{
        }
        break;
      case 'KeyD':
        if(shiftKey){
          board_dup(ctx,canvas);
        }else{
        }
        break;
      case 'Comma':
        if(shiftKey){
        }else{
          ///decrement color id
          let id = ctx.mycolorid;
          id--;
          id += all_colors.length;//ensure it does not go below zero
          id = id % all_colors.length;
          ctx.mycolorid = id;
          board_colorid(id,ctx,canvas);
        }
        break;
      case 'Period':
        if(shiftKey){
        }else{
          ///increment color id
          let id = ctx.mycolorid;
          id++;
          id = id % all_colors.length;
          ctx.mycolorid = id;
          board_colorid(id,ctx,canvas);
        }
        break;
      }
      board_redraw(ctx,canvas);
    }
