    function xyplane_redraw(ctx,canvas){
      ctx.resetTransform();
      ctx.clearRect(0,0,canvas.width,canvas.height);
      ctx.globalAlpha = 1.0;
      ctx.strokeStyle = 'gray';
      ctx.fillStyle = 'gray';
      ctx.lineWidth = 0.5;
      ctx.translate(+ctx.note.origin_x,+ctx.note.origin_y);
      ctx.scale(ctx.note.zoom,ctx.note.zoom);
      var grid = ctx.note.grid;
      ctx.note.y_intercepts = [];
      ctx.note.x_intercepts = [];
      ///////////////////////////////////////////////
      ///figure out x-range
      ///////////////////////////////////////////////
      var left = Math.floor((0 - ctx.note.origin_x)/(ctx.note.grid*ctx.note.zoom));
      var right = Math.ceil((canvas.width - ctx.note.origin_x)/(ctx.note.grid*ctx.note.zoom));
      var D = Math.ceil(ctx.note.grid*ctx.note.zoom)
//console.log('left=',left,'right=',right,'D=',D);
      ///////////////////////////////////////////////
      ///draw gray grids
      ///////////////////////////////////////////////
      ctx.beginPath();
      for (var x=-50; x <= +50; x++) {
        ctx.moveTo(x*grid,  0);
        ctx.lineTo(x*grid,  -50*grid);   
        ctx.moveTo(x*grid,  0);
        ctx.lineTo(x*grid,  +50*grid);   
      }
      for (var y=-50; y <= +50; y++) {
        ctx.moveTo(0,       y*grid);
        ctx.lineTo(-50*grid,y*grid);
        ctx.moveTo(0,       y*grid);
        ctx.lineTo(+50*grid,y*grid);
      }
      ctx.stroke();
      ctx.lineWidth = 1.0;
      ctx.beginPath();
      if(1){
        var y = 0;
        ctx.moveTo(0,       y*grid);
        ctx.lineTo(-50*grid,y*grid);
        ctx.moveTo(0,       y*grid);
        ctx.lineTo(+50*grid,y*grid);
      }
      if(1){
        var x = 0;
        ctx.moveTo(x*grid,  0);
        ctx.lineTo(x*grid,  -50*grid);   
        ctx.moveTo(x*grid,  0);
        ctx.lineTo(x*grid,  +50*grid);   
      }
      ctx.stroke();
      //
      //black labels
      //
      var fs = ctx.note.all_fs;
      var text_fs = ""+fs+"px"+" sans-serif";
      ctx.font = text_fs;
      //
      //horizontal labels
      //
      for(var x=-50; x < +50; x++) {
        ctx.fillText(""+x,x*grid,0);
      }
      //
      //vertical labels
      //
      for(var y=-50; y < +50; y++) {
        if(y==0) continue;
        ctx.fillText(""+y,0,-y*grid);
      }
      ///////////////////////////////////////////////
      ///draw plots  
      ///////////////////////////////////////////////
      if(1){
        ctx.strokeStyle = board_curr_color(ctx,canvas);        
        ctx.fillStyle = board_curr_color(ctx,canvas);        
        ctx.lineWidth = 1.6;
        var g = {};
        var r = 1;
        var s = ctx.note.text.trim();
        var x0 = NaN;
        var y0 = NaN;
        var sign0 = NaN;
        for (var h=left; s && h <= right; h++) {
          for (var d=0; d < D; ++d) {
            var xi = h + d/D;
            g['x'] = xi;
            var [yi] = extract_next_expr(s,g,0);
            var sign = Math.sign(yi);
            if(Number.isFinite(xi) && Number.isFinite(yi)){
              ctx.beginPath();
              if(xi==0){
                //draw y-intercept
                let pathyi = new Path2D();
                pathyi.arc(0,-yi*grid,r+2,0,Math.PI*2);
                ctx.note.y_intercepts.push({pathyi,yi});
//console.log('y-intercept','yi=',yi);
                ctx.arc(0,-yi*grid,r+2,0,Math.PI*2);
              }else{
                ctx.arc(xi*grid,-yi*grid,r,0,Math.PI*2);
              }
              ctx.fill();
            }
            if(Number.isFinite(x0) && Number.isFinite(y0) && 
               Number.isFinite(xi) && Number.isFinite(yi)){
              //draw connecting line
              ctx.beginPath();
              ctx.moveTo(x0*grid,-y0*grid);
              ctx.lineTo(xi*grid,-yi*grid);
              ctx.stroke();
              //draw x-intercept
              if((sign0 < 0 && sign > 0) || (sign0 > 0 && sign < 0 ) || (sign == 0) ){
                let diff = (0 - y0)/(yi - y0);
                let xii = (xi - x0)*diff + x0;  
//console.log('x-intercept','y0=',y0,'yi=',yi,'x0=',x0,'xii=',xii,'sign0=',sign0,'sign=',sign);
                if(Number.isFinite(xi)){
                  var pathxi = new Path2D();
                  pathxi.arc(xii*grid,0,r+2,0,Math.PI*2);
                  ctx.fill(pathxi);
                  ctx.note.x_intercepts.push({pathxi,xi:xii});
                }
              }
            }
            x0 = xi;
            y0 = yi;
            sign0 = sign;
          }
        } 
      }
      ///////////////////////////////////////////////
      ///draw info_text
      ///////////////////////////////////////////////
      if(ctx.note.info_text){
        let info_text = ctx.note.info_text;
        let xi = ctx.note.info_xi;
        let yi = ctx.note.info_yi;
        let fm = ctx.measureText(info_text);
        let h = fs;
        let tx = 4;
        let ty = -4;
        //ctx.clearRect(tx+xi*grid,ty-yi*grid-fm.fontBoundingBoxAscent,fm.width,fm.fontBoundingBoxDescent+fm.fontBoundingBoxAscent);
        let rect = make_rect(tx+xi*grid,ty-yi*grid-fm.fontBoundingBoxAscent,fm.width,fm.fontBoundingBoxDescent+fm.fontBoundingBoxAscent);
        rect = expand_rect(rect,2);
        ctx.clearRect(rect.x,rect.y,rect.w,rect.h);
        ctx.fillText(info_text,tx+xi*grid,ty-yi*grid);
      }
    }
